INSERT INTO `partab` VALUES ('1442', '    17．礼品包装\r\n', '    gift-wrap v．\r\n<br>    Ex. This article can be delivered gift-wrapped .It will be suitable for a Christmas sale.\r\n', 'jjxx11_3_1');
INSERT INTO `partab` VALUES ('1443', '    18．礼品盒\r\n', '       a．gift box\r\n<br>       b．presentation case\r\n', 'jjxx11_3_1');
